package com.nucleus.July25;

/**
 * 
 */
public class ReverseTheList {

	/**
	 * 
	 */
	public ReverseTheList() {
		// TODO Auto-generated constructor stub
	}
	
	public void print(int num) {
		System.out.println("Reversing the list : ");
		for(int i=num; i>=0; i--) {
			System.out.print(i+" ");
		}
	}

}
